let myLibrary=[];let book1=new Book("The Fellowship of the Ring","J. R. R. Tolkien","479",true);let book2=new Book("The Two Towers","J. R. R. Tolkien","415",true);let book3=new Book("The Return of the Kind","J. R. R. Tolkien","347",true);let book4=new Book("The Silmarillion","J. R. R. Tolkien","365",false);const tableBooks=document.getElementById("table-books");const addBookSection=document.getElementById("add-book");myLibrary.push(book1,book2,book3,book4);render();function Book(e,t,o,n){this.title=e;this.author=t;this.pages=o;this.read=n}function render(){tableBooks.innerHTML="";for(let e=0;e<myLibrary.length;e++){let t=myLibrary[e];let o=tableBooks.insertRow(0);let n=document.createElement("button");n.innerText="Delete book";n.classList.add("delete-button");let l=o.insertCell(-1);let r=o.insertCell(-1);let i=o.insertCell(-1);let d=o.insertCell(-1);let a=o.insertCell(-1);l.innerHTML=t.title;r.innerHTML=t.author;i.innerHTML=t.pages;d.innerHTML=t.read;a.appendChild(n)}}let deleteButtons=Array.from(document.querySelectorAll(".delete-button"));deleteButtons.forEach(e=>{e.addEventListener("click",e=>{let t=e.target.parentElement.parentElement;let o=t.parentElement;o.removeChild(t)})});let showFormButton=document.getElementById("show-book__button");showFormButton.onclick=function(){showAddBookSection()};function showAddBookSection(){addBookSection.style.display="block"}function hideAddBookSection(){addBookSection.style.display="none"}let createBookButton=document.getElementById("add-book-form__submit");createBookButton.onclick=function(){let e=this.form[0].value;if(!e){alert("You must enter a Title")}else if(e.trim().length===0){alert("You must enter a Title")}else{addBookToLibrary();hideAddBookSection();resetForm();render()}};function addBookToLibrary(){let e=this.form[0].value;let t=this.form[1].value;let o=this.form[2].value;let n=this.form[3].checked;let l=new Book(e,t,o,n);myLibrary.push(l)}function resetForm(){addBookForm=document.getElementById("add-book-form").reset()}