let myLibrary=[];let book1=new Book("The Fellowship of the Ring","J. R. R. Tolkien","479",true);let book2=new Book("The Two Towers","J. R. R. Tolkien","415",true);let book3=new Book("The Return of the Kind","J. R. R. Tolkien","347",true);let book4=new Book("The Silmarillion","J. R. R. Tolkien","365",false);const tableBooks=document.getElementById("table-books");const addBookSection=document.getElementById("add-book");myLibrary.push(book1,book2,book3,book4);render();function Book(e,o,t,n){this.title=e;this.author=o;this.pages=t;this.read=n}function render(){tableBooks.innerHTML="";for(let e=0;e<myLibrary.length;e++){let o=myLibrary[e];let t=tableBooks.insertRow(0);let n=t.insertCell(0);let l=t.insertCell(1);let i=t.insertCell(2);let r=t.insertCell(3);n.innerHTML=o.title;l.innerHTML=o.author;i.innerHTML=o.pages;r.innerHTML=o.read}}let showFormButton=document.getElementById("show-book__button");showFormButton.onclick=function(){showAddBookSection()};function showAddBookSection(){addBookSection.style.display="block"}function hideAddBookSection(){addBookSection.style.display="none"}let createBookButton=document.getElementById("add-book-form__submit");createBookButton.onclick=function(){let e=this.form[0].value;if(!e){alert("You must enter a Title")}else if(e.trim().length===0){alert("You must enter a Title")}else{addBookToLibrary();hideAddBookSection();resetForm();render()}};function addBookToLibrary(){let e=this.form[0].value;let o=this.form[1].value;let t=this.form[2].value;let n=this.form[3].checked;let l=new Book(e,o,t,n);myLibrary.push(l)}function resetForm(){addBookForm=document.getElementById("add-book-form").reset()}