let myLibrary=[];let book1=new Book("The Fellowship of the Ring","J. R. R. Tolkien","479",true);let book2=new Book("The Two Towers","J. R. R. Tolkien","415",true);let book3=new Book("The Return of the Kind","J. R. R. Tolkien","347",true);let book4=new Book("The Silmarillion","J. R. R. Tolkien","365",false);const table=document.getElementById("table-books");function Book(e,o,t,l){this.title=e;this.author=o;this.pages=t;this.read=l}function render(){for(let e=0;e<myLibrary.length;e++){let o=myLibrary[e];let t=table.insertRow(0);let l=t.insertCell(0);let n=t.insertCell(1);let r=t.insertCell(2);let i=t.insertCell(3);l.innerHTML=o.title;n.innerHTML=o.author;r.innerHTML=o.pages;i.innerHTML=o.read}}myLibrary.push(book1,book2,book3,book4);render();