function Book(e,t,o,n){this.title=e;this.author=t;this.pages=o;this.read=n;this.toggleRead=function(){this.read=!this.read}}let myLibrary=[];let allButtons=[];let book1=new Book("The Fellowship of the Ring","J. R. R. Tolkien","479",true);let book2=new Book("The Two Towers","J. R. R. Tolkien","415",true);let book3=new Book("The Return of the King","J. R. R. Tolkien","347",true);let book4=new Book("The Silmarillion","J. R. R. Tolkien","365",false);const tableBooks=document.getElementById("table-books");const addBookSection=document.getElementById("add-book");myLibrary.push(book1,book2,book3,book4);allButtons.push(toggleButton,deleteButton);renderLibrary();function renderLibrary(){tableBooks.innerHTML="";for(let e=0;e<myLibrary.length;e++){let t=tableBooks.insertRow(-1);t.setAttribute("data-book-index",e);fillRow(e,t)}}function fillRow(e,t){let o=myLibrary[e];for(let n in o){if(o.hasOwnProperty(n)&&n=="read"){insertIcon(e,t.insertCell(-1),o[n])}else if(o.hasOwnProperty(n)&&typeof o[n]!="function"){t.insertCell(-1).innerText=o[n]}else{insertIcon(e,t.insertCell(-1),"delete")}}}function insertIcon(e,t,o){let n=o.toString();let l=createIcon(e,n);t.appendChild(l)}function createIcon(e,t){let o=document.createElement("a");o.setAttribute("data-book-index-delete",e);o.classList.add("material-icons");switch(t){case"delete":o.classList.add("delete");o.innerHTML=t;break;case"true":o.classList.add("check");o.innerHTML="check";break;case"false":o.classList.add("clear");o.innerHTML="clear";break}o.addEventListener("click",function(){switch(t){case"delete":deleteBook(o);break;case"true":toggleRead(o);break;case"false":toggleRead(o);break}});return o}function toggleRead(e){let t=e.parentElement.parentElement;let o=t.dataset.bookIndex;let n=myLibrary[o];t.innerHTML="";n.toggleRead();fillRow(o,t)}function deleteBook(e){let t=e.parentElement.parentElement;let o=t.parentElement;if(confirm("Are you sure you want to delete this book?")){o.removeChild(t)}}let showFormButton=document.getElementById("show-book__button");let cancelBookButton=document.querySelector("#add-book-form__cancel");showFormButton.onclick=function(){showAddBookSection()};cancelBookButton.onclick=function(){resetForm();hideAddBookSection()};function showAddBookSection(){addBookSection.style.display="block"}function hideAddBookSection(){addBookSection.style.display="none"}let createBookButton=document.querySelector("#add-book-form__submit");createBookButton.onclick=function(){let e=this.form[0].value;if(!e||e.trim().length===0){alert("You must enter a Title")}else{addBookToLibrary();hideAddBookSection();resetForm();render()}};function addBookToLibrary(){let e=this.form[0].value;let t=this.form[1].value;let o=this.form[2].value;let n=this.form[3].checked;let l=new Book(e,t,o,n);myLibrary.push(l)}function resetForm(){addBookForm=document.getElementById("add-book-form").reset()}