let myLibrary=[],book1=new Book("The Fellowship of the Ring","J. R. R. Tolkien","479",!0),book2=new Book("The Two Towers","J. R. R. Tolkien","415",!0),book3=new Book("The Return of the Kind","J. R. R. Tolkien","347",!0),book4=new Book("The Silmarillion","J. R. R. Tolkien","365",!1);const tableBooks=document.getElementById("table-books"),addBookSection=document.getElementById("add-book");function Book(e,o,t,n){this.title=e,this.author=o,this.pages=t,this.read=n}function render(){tableBooks.innerHTML="";for(let e=0;e<myLibrary.length;e++){let o=myLibrary[e],t=tableBooks.insertRow(0),n=document.createElement("button");n.innerText="Delete book",n.classList.add("delete-button");let r=t.insertCell(-1),l=t.insertCell(-1),i=t.insertCell(-1),d=t.insertCell(-1),a=t.insertCell(-1);r.innerHTML=o.title,l.innerHTML=o.author,i.innerHTML=o.pages,d.innerHTML=o.read,a.appendChild(n)}}myLibrary.push(book1,book2,book3,book4),render();let deleteButtons=Array.from(document.querySelectorAll(".delete-button"));deleteButtons.forEach(e=>{e.addEventListener("click",e=>{let o=e.target.parentElement.parentElement;o.parentElement.removeChild(o)})});let showFormButton=document.getElementById("show-book__button");function showAddBookSection(){addBookSection.style.display="block"}function hideAddBookSection(){addBookSection.style.display="none"}showFormButton.onclick=function(){showAddBookSection()};let createBookButton=document.getElementById("add-book-form__submit");function addBookToLibrary(){let e=new Book(this.form[0].value,this.form[1].value,this.form[2].value,this.form[3].checked);myLibrary.push(e)}function resetForm(){addBookForm=document.getElementById("add-book-form").reset()}createBookButton.onclick=function(){let e=this.form[0].value;e?0===e.trim().length?alert("You must enter a Title"):(addBookToLibrary(),hideAddBookSection(),resetForm(),render()):alert("You must enter a Title")};